var TE_ui = function(o, parent) {

    var w = window,
        d = document,
        r = this,
        el = parent.el,
        C = 'text-editor',
        target = parent.target,
        field = el('span', {
            'class': C
        }).$,
        controls = el('nav', {
            'class': C + '-controls'
        }).$,
        noop = function() {},

    // default option(s)
    o = extend({
        iconClass: 'fa fa-%s'
    }, o);

    // wrap text area with `<span class="text-editor-field"></span>`
    target.className = C + '-field';
    target.parentNode.insertBefore(field, target);
    field.appendChild(target);
    field.insertBefore(controls, target);

    function is_object(x) {
        return typeof x === "object";
    }

    function extend(a, b) {
        a = a || {};
        for (var i in b) {
            if (is_object(b[i])) {
                a[i] = extend(a[i], b[i]);
            } else {
                a[i] = b[i];
            }
        }
        return a;
    }

    function format(s, a) {
        return s.replace(/%(\d+)s/g, function(b, c) {
            return a[c] ? a[c] : b;
        }).replace(/%s/g, a[0] || '%s');
    }

    function slug(s) {
        return s.toLowerCase().replace(/[^\da-z-]/g, '-').replace(/\-+/g, '-').replace(/^\-|\-$/g, "");
    }

    function control(n, a) {
        var btn = el('a', {
            'href': 'javascript:;',
            'title': a.title || "",
            'tabindex': -1,
            'class': C + '-control ' + C + '-control-' + slug(n)
        }).content(a.text || '<i class="' + format(o.iconClass, [slug(n)]) + '"></i>').on("click", a.click ? function(e) {
            a.click(e, n), e.preventDefault();
        } : noop).$;
        controls.insertBefore(btn, null);
        return r;
    }

    r.parent = parent;
    r.control = control;
    r.controls = function(a) {
        for (var i in a) {
            control(i, a[i]);
        }
        return r;
    };

    r.modal = function(id, H, B, F) {
        var x,
        overlay = el('div', {
            'class': C + '-modal-overlay'
        }).on("click", function() {
            el(overlay).remove();
            el(pane).remove();
        }).appendTo(document.body).$,
        pane = el('div', {
            'class': C + '-modal'
        }).appendTo(document.body).$,
        header = el('div', {
            'class': C + '-modal-header'
        }).appendTo(pane).$,
        body = el('div', {
            'class': C + '-modal-body'
        }).appendTo(pane).$,
        footer = el('div', {
            'class': C + '-modal-footer'
        }).appendTo(pane).$;
    };

    r.parent.ui = r;

    return r;

};

TE.prototype.ui = function(o) {
    return new TE_ui(o, this);
};